// Format events for WhatsApp
const events = $input.all();
const today = new Date().toLocaleDateString('en-US', { 
  weekday: 'long', 
  year: 'numeric', 
  month: 'long', 
  day: 'numeric' 
});

let message = `🗓️ *Your schedule for today*\n📅 ${today}\n\n`;

if (events.length === 0) {
  message += "No events today! 🎉\n\nEnjoy your free day!";
} else {
  message += `You have *${events.length} event${events.length > 1 ? 's' : ''}* today:\n\n`;

  events.forEach((event, index) => {
    const item = event.json;

    // Parse time
    let startTime = 'All day';
    let endTime = '';

    if (item.start.dateTime) {
      const start = new Date(item.start.dateTime);
      const end = new Date(item.end.dateTime);
      startTime = start.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' });
      endTime = ' - ' + end.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' });
    }

    // Emoji based on time of day
    let emoji = '📌';
    if (item.start.dateTime) {
      const hour = new Date(item.start.dateTime).getHours();
      if (hour < 12) emoji = '🌅';
      else if (hour < 17) emoji = '☀️';
      else emoji = '🌙';
    }

    message += `${emoji} *${startTime}${endTime}*\n`;
    message += `   📋 ${item.summary || 'No title'}\n`;

    // Add location if available
    if (item.location) {
      message += `   📍 ${item.location}\n`;
    }

    // Add description if available (shortened)
    if (item.description) {
      const shortDesc = item.description.substring(0, 100);
      const desc = item.description.length > 100 ? shortDesc + '...' : shortDesc;
      message += `   💬 ${desc}\n`;
    }

    // Meeting link if available
    if (item.hangoutLink) {
      message += `   🔗 Online meeting available\n`;
    }

    message += '\n';
  });
}

return [{
  json: {
    message: message,
    eventCount: events.length
  }
}];
