// Parse the analysis and generate 10 different infographic prompts
const analysis = JSON.parse($input.first().json.response);
const baseText = $('Manual Trigger').first().json.postText;

const infographicStyles = [
  {
    style: "Minimalist Data Visualization",
    prompt: `Create a clean, minimalist infographic with data visualization. Title: "${analysis.title}". Show ${analysis.statistics.join(', ')} as modern charts and graphs. Use a professional blue and white color scheme. Corporate style, vector graphics, high contrast.`
  },
  {
    style: "Timeline Infographic",
    prompt: `Design a horizontal timeline infographic showing the progression of ${analysis.mainMessage}. Include ${analysis.keyPoints.length} key milestones with icons. Modern gradient colors from purple to blue. Clean typography, business professional.`
  },
  {
    style: "Comparison Chart",
    prompt: `Create a before/after comparison infographic. Left side: 'Traditional Approach' in muted colors. Right side: '${analysis.title}' in vibrant colors. Include statistics: ${analysis.statistics.join(', ')}. Split design, contrasting colors.`
  },
  {
    style: "Icon-Based Layout",
    prompt: `Design an icon-based infographic grid. ${analysis.keyPoints.length} sections, each with a unique flat icon and short text. Title: "${analysis.title}". Colorful flat design, geometric shapes, modern icons.`
  },
  {
    style: "Flowchart Process",
    prompt: `Create a flowchart infographic showing the process of ${analysis.mainMessage}. Connect ${analysis.keyPoints.length} steps with arrows. Use gradient backgrounds, modern UI elements. Tech-style design.`
  },
  {
    style: "Statistical Dashboard",
    prompt: `Design a dashboard-style infographic with multiple data widgets. Show ${analysis.statistics.join(', ')} as gauges, progress bars, and pie charts. Dark mode theme with neon accents. Futuristic data visualization.`
  },
  {
    style: "Pyramid Hierarchy",
    prompt: `Create a pyramid infographic with ${analysis.keyPoints.length} levels. Bottom to top progression showing ${analysis.mainMessage}. Each level different color, professional gradient. Business hierarchy visualization.`
  },
  {
    style: "Circular Diagram",
    prompt: `Design a circular/radial infographic. Center: "${analysis.title}". Radiating sections for each key point. Include ${analysis.statistics[0] || '30%'} as prominent statistic. Mandala-like business diagram.`
  },
  {
    style: "Magazine Layout",
    prompt: `Create a magazine-style infographic layout. Bold headline: "${analysis.title}". Mix of text blocks, pull quotes, and data visualizations. Editorial design, professional typography, asymmetric layout.`
  },
  {
    style: "3D Isometric",
    prompt: `Design an isometric 3D infographic. Show ${analysis.keyPoints.length} interconnected blocks or buildings. Each represents a benefit. Include floating data labels. Modern 3D illustration style, tech company aesthetic.`
  }
];

return infographicStyles.map((item, index) => ({
  json: {
    ...item,
    index: index + 1,
    analysis: analysis,
    originalText: baseText,
    aspectRatio: "16:9",
    size: "1792x1024"
  }
}));

// Compile final output with all generated images and metadata
const items = $input.all();
const originalText = items[0].json.originalText;
const analysis = items[0].json.analysis;

const output = {
  linkedInPost: originalText,
  analysis: analysis,
  generatedImages: items.map(item => ({
    style: item.json.style,
    fileName: `infographic_${item.json.index}_${item.json.style.replace(/ /g, '_').toLowerCase()}.png`,
    prompt: item.json.prompt,
    index: item.json.index
  })),
  totalImages: items.length,
  timestamp: new Date().toISOString(),
  usage: {
    recommendation: "Choose the infographic style that best matches your brand and audience. Post with your original text on LinkedIn for maximum engagement.",
    tips: [
      "Use carousel posts for multiple images",
      "Add relevant hashtags",
      "Post during peak engagement hours (Tue-Thu, 8-10 AM or 5-6 PM)",
      "Engage with comments in the first hour"
    ]
  }
};

return [{ json: output }];
